package ua.ksstroy.converter.zonegroup;import ua.ksstroy.converter.Converter;import ua.ksstroy.logic.zonegroup.Zone;import ua.ksstroy.models.zone.ZonesModel;public class ZoneToZoneModelConverter implements Converter<Zone, ZonesModel> {    public ZonesModel convert(Zone zone) {        ZonesModel zonesModel = new ZonesModel();        zonesModel.setId(zone.getId());        zonesModel.setName(zone.getName());        zonesModel.setHeight(zone.getHeight());        zonesModel.setWidth(zone.getWidth());        zonesModel.setMeasureName(zone.getMeasure());        return zonesModel;    }}