package ua.ksstroy.web;import ua.ksstroy.logic.worktype.WorkType;import ua.ksstroy.logic.worktype.WorkTypeGroup;import ua.ksstroy.logic.zone.Measure;import java.util.ArrayList;import java.util.List;/** * Created by Zheka on 30.11.2015. */public class WorkTypeMock {    private String id = "1";    private String description = "bla-bla-bla";    private String measure = "M2";    private Double unitPrice = 3.0;    public WorkTypeGroup getWorkTypeObject() {        WorkTypeGroup outerWorkTypeGroup = new WorkTypeGroup();        outerWorkTypeGroup.setId(Integer.parseInt(id));        outerWorkTypeGroup.setName("OuterWorkTypeGroup");/************************* */        List<WorkTypeGroup> workTypeGroupList = new ArrayList<>();        WorkTypeGroup workTypeGroup1 = new WorkTypeGroup();        workTypeGroup1.setId(1);        workTypeGroup1.setName("InnerWorkTypeGroup1");        WorkTypeGroup workTypeGroup2 = new WorkTypeGroup();        workTypeGroup2.setId(2);        workTypeGroup2.setName("InnerWorkTypeGroup2");/****************** */        List<WorkType> workTypeList = new ArrayList<>();        WorkType workType1 = new WorkType();        workType1.setId(1);        workType1.setName("WorkType1");        workType1.setDescription(description);        workType1.setMeasure(Measure.valueOf(measure));        workType1.setUnitPrice(unitPrice);        WorkType workType2 = new WorkType();        workType2.setId(2);        workType2.setName("WorkType2");        workType2.setDescription(description);        workType2.setMeasure(Measure.valueOf(measure));        workType2.setUnitPrice(unitPrice);        workTypeList.add(workType1);        workTypeList.add(workType2);/******************* */        workTypeGroup1.setWorkTypes(workTypeList);        workTypeGroupList.add(workTypeGroup1);        workTypeGroupList.add(workTypeGroup2);        outerWorkTypeGroup.setWorkTypeGroups(workTypeGroupList);        return outerWorkTypeGroup;    }}